using System.Collections.Generic;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using Newtonsoft.Json;

namespace DoenaSoft.JsonFragmentParser.Tests;

[TestClass]
public sealed class EndOfJsonParserTests
{
    [TestMethod]
    public void ObjectNormal()
    {
        const string Input = "{ \"Name\":\"DJ\" }";

        var cleaned = Clean(Input);

        const string Expected = Input;

        Assert.AreEqual(Expected, cleaned);

        JsonPrinter.PrintInput(cleaned);
    }

    [TestMethod]
    public void ObjectSuffix()
    {
        const string Input = "{ \"Name\":\"DJ\" }blabla";

        var cleaned = Clean(Input);

        const string Expected = "{ \"Name\":\"DJ\" }";

        Assert.AreEqual(Expected, cleaned);

        JsonPrinter.PrintInput(cleaned);
    }

    [TestMethod]
    public void ObjectEscapedQuote()
    {
        const string Input = "{ \"Name\":\"D\\\"J\" }";

        var cleaned = Clean(Input);

        const string Expected = Input;

        Assert.AreEqual(Expected, cleaned);

        JsonPrinter.PrintInput(cleaned);
    }

    [TestMethod]
    public void ObjectEscapedBracket()
    {
        const string Input = "{ \"Name\":\"D[J\" }";

        var cleaned = Clean(Input);

        const string Expected = Input;

        Assert.AreEqual(Expected, cleaned);

        JsonPrinter.PrintInput(cleaned);
    }

    [TestMethod]
    public void ObjectEscapedBrace()
    {
        const string Input = "{ \"Name\":\"D}J\" }";

        var cleaned = Clean(Input);

        Assert.AreEqual(Input, cleaned);

        JsonPrinter.PrintInput(cleaned);
    }

    [TestMethod]
    public void RootList()
    {
        var source = new List<Person>()
        {
            new() { Name = "DJ" },
            new() { Name = "Doena" },
        };

        var input = JsonConvert.SerializeObject(source);

        var cleaned = Clean(input);

        var expected = input;

        Assert.AreEqual(expected, cleaned);

        JsonPrinter.PrintInput(cleaned);
    }

    [TestMethod]
    public void AnonymousRootList()
    {
        var source = new[]
        {
            new { Name= "DJ" },
            new { Name= "Doena" },
        };

        var input = JsonConvert.SerializeObject(source);

        var cleaned = Clean(input);

        var expected = input;

        Assert.AreEqual(expected, cleaned);

        JsonPrinter.PrintInput(cleaned);
    }

    [TestMethod]
    public void List()
    {
        const string Input = "{ \"items\": [ { \"Name\":\"DJ\" }, { \"Name\":\"Doena\" } ] }";

        var cleaned = Clean(Input);

        const string Expected = Input;

        Assert.AreEqual(Expected, cleaned);

        JsonPrinter.PrintInput(cleaned);
    }

